describe('parseImplementReadyMarker function', () => {
  it('returns null when content has no marker', () => {
    const result = parseImplementReadyMarker('无标记内容');
    expect(result).toBeNull();
  });

  it('returns default object when marker present but no JSON inside', () => {
    const content = '[IMPLEMENT_READY][/IMPLEMENT_READY]';
    const result = parseImplementReadyMarker(content);
    expect(result).toMatchObject({
      requirement: '用户需求已确认',
      technicalPlan: '',
      implementationSteps: [],
    });
  });

  it('parses JSON content correctly', () => {
    const json = JSON.stringify({
      requirement: '需求A',
      technicalPlan: '方案B',
      steps: ['一步', '二步'],
    });
    const content = `[IMPLEMENT_READY]${json}[/IMPLEMENT_READY]`;
    const result = parseImplementReadyMarker(content);
    expect(result).toMatchObject({
      requirement: '需求A',
      technicalPlan: '方案B',
      implementationSteps: ['一步', '二步'],
    });
  });

  it('handles malformed JSON and falls back to text content', () => {
    const content = `[IMPLEMENT_READY]{invalid json}[/IMPLEMENT_READY]`;
    const result = parseImplementReadyMarker(content);
    expect(result).toMatchObject({
      requirement: '用户需求已确认',
      technicalPlan: '{invalid json}',
      implementationSteps: [],
    });
  });

  it('handles missing fields in JSON gracefully', () => {
    const json = JSON.stringify({ requirement: '需求' });
    const content = `[IMPLEMENT_READY]${json}[/IMPLEMENT_READY]`;
    const result = parseImplementReadyMarker(content);
    expect(result).toMatchObject({
      requirement: '需求',
      technicalPlan: '',
      implementationSteps: [],
    });
  });
});

